import React, { useState } from 'react';
import Header from './components/Header';
import CodeEditor from './components/CodeEditor';
import TestGenerator from './components/TestGenerator';
import VulnerabilityChecker from './components/VulnerabilityChecker';
import TestRunner from './components/TestRunner';
import { Code, TestTube, Shield, Play } from 'lucide-react';

function App() {
  const [activeTab, setActiveTab] = useState('editor');
  const [sourceCode, setSourceCode] = useState('');
  const [generatedTests, setGeneratedTests] = useState('');
  const [vulnerabilities, setVulnerabilities] = useState([]);
  const [testResults, setTestResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const tabs = [
    { id: 'editor', label: 'Code Editor', icon: Code },
    { id: 'tests', label: 'Test Generator', icon: TestTube },
    { id: 'security', label: 'Vulnerability Checker', icon: Shield },
    { id: 'runner', label: 'Test Runner', icon: Play },
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      
      <main className="container mx-auto px-4 py-8">
        {/* Tab Navigation */}
        <div className="flex space-x-1 mb-6 bg-white rounded-lg p-1 shadow-sm">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center space-x-2 px-4 py-2 rounded-md font-medium transition-colors duration-200 ${
                  activeTab === tab.id
                    ? 'bg-primary-100 text-primary-700'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                }`}
              >
                <Icon size={18} />
                <span>{tab.label}</span>
              </button>
            );
          })}
        </div>

        {/* Tab Content */}
        <div className="space-y-6">
          {activeTab === 'editor' && (
            <CodeEditor
              sourceCode={sourceCode}
              setSourceCode={setSourceCode}
            />
          )}
          
          {activeTab === 'tests' && (
            <TestGenerator
              sourceCode={sourceCode}
              generatedTests={generatedTests}
              setGeneratedTests={setGeneratedTests}
              isLoading={isLoading}
              setIsLoading={setIsLoading}
            />
          )}
          
          {activeTab === 'security' && (
            <VulnerabilityChecker
              sourceCode={sourceCode}
              vulnerabilities={vulnerabilities}
              setVulnerabilities={setVulnerabilities}
              isLoading={isLoading}
              setIsLoading={setIsLoading}
            />
          )}
          
          {activeTab === 'runner' && (
            <TestRunner
              sourceCode={sourceCode}
              generatedTests={generatedTests}
              testResults={testResults}
              setTestResults={setTestResults}
              isLoading={isLoading}
              setIsLoading={setIsLoading}
            />
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
