import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import { Sparkles, Copy, Check, Download, Play } from 'lucide-react';
import { generateTestsWithAI } from '../services/aiService';

const TestGenerator = ({ sourceCode, generatedTests, setGeneratedTests, isLoading, setIsLoading }) => {
  const [copied, setCopied] = useState(false);
  const [testFramework, setTestFramework] = useState('jest');
  const [coverage, setCoverage] = useState('high');

  const handleGenerateTests = async () => {
    if (!sourceCode.trim()) {
      alert('Please enter some source code first!');
      return;
    }

    setIsLoading(true);
    try {
      const tests = await generateTestsWithAI(sourceCode, testFramework, coverage);
      setGeneratedTests(tests);
    } catch (error) {
      console.error('Error generating tests:', error);
      alert('Failed to generate tests. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(generatedTests);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const handleDownload = () => {
    const blob = new Blob([generatedTests], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `tests.${testFramework === 'jest' ? 'js' : 'js'}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getTestFrameworkLanguage = () => {
    switch (testFramework) {
      case 'jest':
        return 'javascript';
      case 'mocha':
        return 'javascript';
      case 'jasmine':
        return 'javascript';
      default:
        return 'javascript';
    }
  };

  return (
    <div className="space-y-4">
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-gray-900">AI Test Generator</h2>
          <div className="flex items-center space-x-2">
            <select
              value={testFramework}
              onChange={(e) => setTestFramework(e.target.value)}
              className="input-field text-sm"
            >
              <option value="jest">Jest</option>
              <option value="mocha">Mocha</option>
              <option value="jasmine">Jasmine</option>
            </select>
            
            <select
              value={coverage}
              onChange={(e) => setCoverage(e.target.value)}
              className="input-field text-sm"
            >
              <option value="basic">Basic Coverage</option>
              <option value="medium">Medium Coverage</option>
              <option value="high">High Coverage</option>
              <option value="comprehensive">Comprehensive</option>
            </select>
          </div>
        </div>

        <div className="flex items-center space-x-2 mb-4">
          <button
            onClick={handleGenerateTests}
            disabled={!sourceCode.trim() || isLoading}
            className="btn-primary flex items-center space-x-2"
          >
            <Sparkles size={16} />
            <span>{isLoading ? 'Generating...' : 'Generate Tests'}</span>
          </button>
          
          {generatedTests && (
            <>
              <button
                onClick={handleCopy}
                className="btn-secondary text-sm"
              >
                {copied ? (
                  <>
                    <Check size={16} className="inline mr-1" />
                    Copied!
                  </>
                ) : (
                  <>
                    <Copy size={16} className="inline mr-1" />
                    Copy
                  </>
                )}
              </button>
              
              <button
                onClick={handleDownload}
                className="btn-secondary text-sm"
              >
                <Download size={16} className="inline mr-1" />
                Download
              </button>
            </>
          )}
        </div>

        {!sourceCode.trim() && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center space-x-2">
              <Play size={20} className="text-yellow-600" />
              <p className="text-yellow-800">
                Please add some source code in the Code Editor tab first, then come back to generate tests.
              </p>
            </div>
          </div>
        )}

        {isLoading && (
          <div className="flex items-center justify-center py-8">
            <div className="flex items-center space-x-3">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary-600"></div>
              <span className="text-gray-600">AI is generating comprehensive tests...</span>
            </div>
          </div>
        )}

        {generatedTests && !isLoading && (
          <div className="border border-gray-200 rounded-lg overflow-hidden">
            <div className="bg-gray-50 px-4 py-2 border-b border-gray-200">
              <h3 className="text-sm font-medium text-gray-700">
                Generated Tests ({testFramework.toUpperCase()})
              </h3>
            </div>
            <Editor
              height="500px"
              language={getTestFrameworkLanguage()}
              value={generatedTests}
              theme="vs-dark"
              options={{
                readOnly: true,
                minimap: { enabled: false },
                fontSize: 14,
                lineNumbers: 'on',
                roundedSelection: false,
                scrollBeyondLastLine: false,
                automaticLayout: true,
                wordWrap: 'on',
              }}
            />
          </div>
        )}
      </div>

      {generatedTests && (
        <div className="card">
          <h3 className="text-md font-semibold text-gray-900 mb-2">Test Statistics</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary-600">
                {generatedTests.split('test(').length - 1 + generatedTests.split('it(').length - 1}
              </div>
              <div className="text-gray-600">Test Cases</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-success-600">
                {generatedTests.split('describe(').length - 1}
              </div>
              <div className="text-gray-600">Test Suites</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-warning-600">
                {generatedTests.split('\n').length}
              </div>
              <div className="text-gray-600">Lines of Tests</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-info-600">
                {Math.round(generatedTests.length / 1024 * 100) / 100}
              </div>
              <div className="text-gray-600">KB</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TestGenerator;
