import React, { useState } from 'react';
import { Shield, AlertTriangle, CheckCircle, XCircle, Info } from 'lucide-react';
import { checkVulnerabilities } from '../services/securityService';

const VulnerabilityChecker = ({ sourceCode, vulnerabilities, setVulnerabilities, isLoading, setIsLoading }) => {
  const [scanLevel, setScanLevel] = useState('comprehensive');

  const handleScanVulnerabilities = async () => {
    if (!sourceCode.trim()) {
      alert('Please enter some source code first!');
      return;
    }

    setIsLoading(true);
    try {
      const results = await checkVulnerabilities(sourceCode, scanLevel);
      setVulnerabilities(results);
    } catch (error) {
      console.error('Error scanning vulnerabilities:', error);
      alert('Failed to scan for vulnerabilities. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const getSeverityColor = (severity) => {
    switch (severity.toLowerCase()) {
      case 'critical':
        return 'text-danger-600 bg-danger-50 border-danger-200';
      case 'high':
        return 'text-danger-600 bg-danger-50 border-danger-200';
      case 'medium':
        return 'text-warning-600 bg-warning-50 border-warning-200';
      case 'low':
        return 'text-info-600 bg-info-50 border-info-200';
      default:
        return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const getSeverityIcon = (severity) => {
    switch (severity.toLowerCase()) {
      case 'critical':
      case 'high':
        return <XCircle size={20} className="text-danger-600" />;
      case 'medium':
        return <AlertTriangle size={20} className="text-warning-600" />;
      case 'low':
        return <Info size={20} className="text-info-600" />;
      default:
        return <Info size={20} className="text-gray-600" />;
    }
  };

  const getSecurityScore = () => {
    if (!vulnerabilities.length) return 100;
    
    const totalVulnerabilities = vulnerabilities.length;
    const criticalCount = vulnerabilities.filter(v => v.severity.toLowerCase() === 'critical').length;
    const highCount = vulnerabilities.filter(v => v.severity.toLowerCase() === 'high').length;
    const mediumCount = vulnerabilities.filter(v => v.severity.toLowerCase() === 'medium').length;
    const lowCount = vulnerabilities.filter(v => v.severity.toLowerCase() === 'low').length;
    
    const score = 100 - (criticalCount * 25 + highCount * 15 + mediumCount * 8 + lowCount * 3);
    return Math.max(0, score);
  };

  return (
    <div className="space-y-4">
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-gray-900">Security Vulnerability Scanner</h2>
          <div className="flex items-center space-x-2">
            <select
              value={scanLevel}
              onChange={(e) => setScanLevel(e.target.value)}
              className="input-field text-sm"
            >
              <option value="basic">Basic Scan</option>
              <option value="standard">Standard Scan</option>
              <option value="comprehensive">Comprehensive Scan</option>
              <option value="deep">Deep Scan</option>
            </select>
          </div>
        </div>

        <div className="flex items-center space-x-2 mb-4">
          <button
            onClick={handleScanVulnerabilities}
            disabled={!sourceCode.trim() || isLoading}
            className="btn-warning flex items-center space-x-2"
          >
            <Shield size={16} />
            <span>{isLoading ? 'Scanning...' : 'Scan for Vulnerabilities'}</span>
          </button>
        </div>

        {!sourceCode.trim() && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center space-x-2">
              <AlertTriangle size={20} className="text-yellow-600" />
              <p className="text-yellow-800">
                Please add some source code in the Code Editor tab first, then come back to scan for vulnerabilities.
              </p>
            </div>
          </div>
        )}

        {isLoading && (
          <div className="flex items-center justify-center py-8">
            <div className="flex items-center space-x-3">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-warning-600"></div>
              <span className="text-gray-600">Scanning for security vulnerabilities...</span>
            </div>
          </div>
        )}
      </div>

      {vulnerabilities.length > 0 && !isLoading && (
        <>
          <div className="card">
            <h3 className="text-md font-semibold text-gray-900 mb-4">Security Overview</h3>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <div className="text-3xl font-bold text-gray-900">{getSecurityScore()}</div>
                <div className="text-sm text-gray-600">Security Score</div>
              </div>
              <div className="text-center p-4 bg-danger-50 rounded-lg">
                <div className="text-3xl font-bold text-danger-600">
                  {vulnerabilities.filter(v => ['critical', 'high'].includes(v.severity.toLowerCase())).length}
                </div>
                <div className="text-sm text-danger-600">High/Critical Issues</div>
              </div>
              <div className="text-center p-4 bg-warning-50 rounded-lg">
                <div className="text-3xl font-bold text-warning-600">
                  {vulnerabilities.filter(v => v.severity.toLowerCase() === 'medium').length}
                </div>
                <div className="text-sm text-warning-600">Medium Issues</div>
              </div>
              <div className="text-center p-4 bg-info-50 rounded-lg">
                <div className="text-3xl font-bold text-info-600">
                  {vulnerabilities.filter(v => v.severity.toLowerCase() === 'low').length}
                </div>
                <div className="text-sm text-info-600">Low Issues</div>
              </div>
            </div>
          </div>

          <div className="card">
            <h3 className="text-md font-semibold text-gray-900 mb-4">Vulnerability Details</h3>
            <div className="space-y-3">
              {vulnerabilities.map((vulnerability, index) => (
                <div
                  key={index}
                  className={`border rounded-lg p-4 ${getSeverityColor(vulnerability.severity)}`}
                >
                  <div className="flex items-start space-x-3">
                    {getSeverityIcon(vulnerability.severity)}
                    <div className="flex-1">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-semibold">{vulnerability.title}</h4>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          vulnerability.severity.toLowerCase() === 'critical' || vulnerability.severity.toLowerCase() === 'high'
                            ? 'bg-danger-100 text-danger-800'
                            : vulnerability.severity.toLowerCase() === 'medium'
                            ? 'bg-warning-100 text-warning-800'
                            : 'bg-info-100 text-info-800'
                        }`}>
                          {vulnerability.severity.toUpperCase()}
                        </span>
                      </div>
                      <p className="text-sm mb-2">{vulnerability.description}</p>
                      {vulnerability.line && (
                        <p className="text-xs text-gray-600 mb-2">
                          Line: {vulnerability.line}
                        </p>
                      )}
                      {vulnerability.recommendation && (
                        <div className="bg-white bg-opacity-50 rounded p-2">
                          <p className="text-xs font-medium mb-1">Recommendation:</p>
                          <p className="text-xs">{vulnerability.recommendation}</p>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}

      {vulnerabilities.length === 0 && !isLoading && sourceCode.trim() && (
        <div className="card">
          <div className="flex items-center justify-center py-8">
            <div className="text-center">
              <CheckCircle size={48} className="text-success-600 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-success-600 mb-2">No Vulnerabilities Found!</h3>
              <p className="text-gray-600">Your code appears to be secure. Great job!</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VulnerabilityChecker;
