import React, { useState } from 'react';
import Editor from '@monaco-editor/react';
import { Download, Upload, Copy, Check } from 'lucide-react';

const CodeEditor = ({ sourceCode, setSourceCode }) => {
  const [copied, setCopied] = useState(false);
  const [language, setLanguage] = useState('javascript');

  const sampleCode = `// Sample JavaScript function
function calculateSum(a, b) {
  if (typeof a !== 'number' || typeof b !== 'number') {
    throw new Error('Both arguments must be numbers');
  }
  return a + b;
}

// Sample class
class Calculator {
  constructor() {
    this.history = [];
  }
  
  add(a, b) {
    const result = calculateSum(a, b);
    this.history.push({ operation: 'add', a, b, result });
    return result;
  }
  
  getHistory() {
    return this.history;
  }
}

module.exports = { calculateSum, Calculator };`;

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(sourceCode);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setSourceCode(e.target.result);
      };
      reader.readAsText(file);
    }
  };

  const handleDownload = () => {
    const blob = new Blob([sourceCode], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'source-code.js';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const loadSampleCode = () => {
    setSourceCode(sampleCode);
  };

  return (
    <div className="space-y-4">
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-gray-900">Code Editor</h2>
          <div className="flex items-center space-x-2">
            <select
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
              className="input-field text-sm"
            >
              <option value="javascript">JavaScript</option>
              <option value="typescript">TypeScript</option>
              <option value="python">Python</option>
              <option value="java">Java</option>
              <option value="cpp">C++</option>
            </select>
          </div>
        </div>

        <div className="flex items-center space-x-2 mb-4">
          <button
            onClick={loadSampleCode}
            className="btn-secondary text-sm"
          >
            Load Sample Code
          </button>
          
          <label className="btn-secondary text-sm cursor-pointer">
            <Upload size={16} className="inline mr-1" />
            Upload File
            <input
              type="file"
              accept=".js,.ts,.py,.java,.cpp,.c,.cs"
              onChange={handleFileUpload}
              className="hidden"
            />
          </label>
          
          <button
            onClick={handleDownload}
            className="btn-secondary text-sm"
            disabled={!sourceCode}
          >
            <Download size={16} className="inline mr-1" />
            Download
          </button>
          
          <button
            onClick={handleCopy}
            className="btn-secondary text-sm"
            disabled={!sourceCode}
          >
            {copied ? (
              <>
                <Check size={16} className="inline mr-1" />
                Copied!
              </>
            ) : (
              <>
                <Copy size={16} className="inline mr-1" />
                Copy
              </>
            )}
          </button>
        </div>

        <div className="border border-gray-200 rounded-lg overflow-hidden">
          <Editor
            height="500px"
            language={language}
            value={sourceCode}
            onChange={setSourceCode}
            theme="vs-dark"
            options={{
              minimap: { enabled: false },
              fontSize: 14,
              lineNumbers: 'on',
              roundedSelection: false,
              scrollBeyondLastLine: false,
              automaticLayout: true,
              wordWrap: 'on',
              suggestOnTriggerCharacters: true,
              quickSuggestions: true,
            }}
          />
        </div>
      </div>

      {sourceCode && (
        <div className="card">
          <h3 className="text-md font-semibold text-gray-900 mb-2">Code Statistics</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary-600">
                {sourceCode.split('\n').length}
              </div>
              <div className="text-gray-600">Lines of Code</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-success-600">
                {sourceCode.split('function').length - 1 + sourceCode.split('class').length - 1}
              </div>
              <div className="text-gray-600">Functions/Classes</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-warning-600">
                {sourceCode.length}
              </div>
              <div className="text-gray-600">Characters</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-info-600">
                {Math.round(sourceCode.length / 1024 * 100) / 100}
              </div>
              <div className="text-gray-600">KB</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CodeEditor;
