import React, { useState } from 'react';
import { Play, CheckCircle, XCircle, Clock, BarChart3 } from 'lucide-react';
import { runTests } from '../services/testRunnerService';

const TestRunner = ({ sourceCode, generatedTests, testResults, setTestResults, isLoading, setIsLoading }) => {
  const [testEnvironment, setTestEnvironment] = useState('node');

  const handleRunTests = async () => {
    if (!sourceCode.trim() || !generatedTests.trim()) {
      alert('Please generate tests first before running them!');
      return;
    }

    setIsLoading(true);
    try {
      const results = await runTests(sourceCode, generatedTests, testEnvironment);
      setTestResults(results);
    } catch (error) {
      console.error('Error running tests:', error);
      alert('Failed to run tests. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const getTestStatusIcon = (status) => {
    switch (status) {
      case 'passed':
        return <CheckCircle size={16} className="text-success-600" />;
      case 'failed':
        return <XCircle size={16} className="text-danger-600" />;
      case 'skipped':
        return <Clock size={16} className="text-warning-600" />;
      default:
        return <Clock size={16} className="text-gray-600" />;
    }
  };

  const getTestStatusColor = (status) => {
    switch (status) {
      case 'passed':
        return 'bg-success-50 border-success-200 text-success-800';
      case 'failed':
        return 'bg-danger-50 border-danger-200 text-danger-800';
      case 'skipped':
        return 'bg-warning-50 border-warning-200 text-warning-800';
      default:
        return 'bg-gray-50 border-gray-200 text-gray-800';
    }
  };

  const getCoveragePercentage = () => {
    if (!testResults) return 0;
    const total = testResults.totalTests || 0;
    const passed = testResults.passedTests || 0;
    return total > 0 ? Math.round((passed / total) * 100) : 0;
  };

  return (
    <div className="space-y-4">
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-lg font-semibold text-gray-900">Test Runner</h2>
          <div className="flex items-center space-x-2">
            <select
              value={testEnvironment}
              onChange={(e) => setTestEnvironment(e.target.value)}
              className="input-field text-sm"
            >
              <option value="node">Node.js</option>
              <option value="browser">Browser</option>
              <option value="jest">Jest Environment</option>
            </select>
          </div>
        </div>

        <div className="flex items-center space-x-2 mb-4">
          <button
            onClick={handleRunTests}
            disabled={!sourceCode.trim() || !generatedTests.trim() || isLoading}
            className="btn-success flex items-center space-x-2"
          >
            <Play size={16} />
            <span>{isLoading ? 'Running Tests...' : 'Run Tests'}</span>
          </button>
        </div>

        {(!sourceCode.trim() || !generatedTests.trim()) && (
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-center space-x-2">
              <Clock size={20} className="text-yellow-600" />
              <p className="text-yellow-800">
                Please generate tests in the Test Generator tab first, then come back to run them.
              </p>
            </div>
          </div>
        )}

        {isLoading && (
          <div className="flex items-center justify-center py-8">
            <div className="flex items-center space-x-3">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-success-600"></div>
              <span className="text-gray-600">Running tests in {testEnvironment} environment...</span>
            </div>
          </div>
        )}
      </div>

      {testResults && !isLoading && (
        <>
          <div className="card">
            <h3 className="text-md font-semibold text-gray-900 mb-4">Test Results Overview</h3>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="text-center p-4 bg-gray-50 rounded-lg">
                <div className="text-3xl font-bold text-gray-900">{testResults.totalTests || 0}</div>
                <div className="text-sm text-gray-600">Total Tests</div>
              </div>
              <div className="text-center p-4 bg-success-50 rounded-lg">
                <div className="text-3xl font-bold text-success-600">{testResults.passedTests || 0}</div>
                <div className="text-sm text-success-600">Passed</div>
              </div>
              <div className="text-center p-4 bg-danger-50 rounded-lg">
                <div className="text-3xl font-bold text-danger-600">{testResults.failedTests || 0}</div>
                <div className="text-sm text-danger-600">Failed</div>
              </div>
              <div className="text-center p-4 bg-primary-50 rounded-lg">
                <div className="text-3xl font-bold text-primary-600">{getCoveragePercentage()}%</div>
                <div className="text-sm text-primary-600">Coverage</div>
              </div>
            </div>
          </div>

          <div className="card">
            <h3 className="text-md font-semibold text-gray-900 mb-4">Test Execution Details</h3>
            
            {testResults.executionTime && (
              <div className="mb-4 p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Clock size={16} className="text-gray-600" />
                  <span className="text-sm text-gray-600">
                    Execution Time: {testResults.executionTime}ms
                  </span>
                </div>
              </div>
            )}

            {testResults.testSuites && testResults.testSuites.length > 0 && (
              <div className="space-y-4">
                {testResults.testSuites.map((suite, suiteIndex) => (
                  <div key={suiteIndex} className="border border-gray-200 rounded-lg overflow-hidden">
                    <div className="bg-gray-50 px-4 py-2 border-b border-gray-200">
                      <h4 className="font-medium text-gray-900">{suite.name}</h4>
                    </div>
                    <div className="p-4">
                      <div className="space-y-2">
                        {suite.tests.map((test, testIndex) => (
                          <div
                            key={testIndex}
                            className={`flex items-center justify-between p-3 rounded-lg border ${getTestStatusColor(test.status)}`}
                          >
                            <div className="flex items-center space-x-3">
                              {getTestStatusIcon(test.status)}
                              <div>
                                <p className="font-medium">{test.name}</p>
                                {test.duration && (
                                  <p className="text-xs text-gray-600">
                                    Duration: {test.duration}ms
                                  </p>
                                )}
                              </div>
                            </div>
                            <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                              test.status === 'passed'
                                ? 'bg-success-100 text-success-800'
                                : test.status === 'failed'
                                ? 'bg-danger-100 text-danger-800'
                                : 'bg-warning-100 text-warning-800'
                            }`}>
                              {test.status.toUpperCase()}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {testResults.errors && testResults.errors.length > 0 && (
              <div className="mt-4">
                <h4 className="font-medium text-gray-900 mb-2">Test Errors</h4>
                <div className="space-y-2">
                  {testResults.errors.map((error, index) => (
                    <div key={index} className="bg-danger-50 border border-danger-200 rounded-lg p-3">
                      <p className="text-sm font-medium text-danger-800">{error.test}</p>
                      <p className="text-xs text-danger-600 mt-1">{error.message}</p>
                      {error.stack && (
                        <pre className="text-xs text-danger-600 mt-2 bg-white bg-opacity-50 p-2 rounded overflow-x-auto">
                          {error.stack}
                        </pre>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="card">
            <h3 className="text-md font-semibold text-gray-900 mb-4">Coverage Report</h3>
            <div className="space-y-4">
              <div className="flex items-center space-x-2">
                <BarChart3 size={20} className="text-primary-600" />
                <span className="text-sm font-medium">Code Coverage</span>
              </div>
              
              <div className="w-full bg-gray-200 rounded-full h-4">
                <div
                  className="bg-primary-600 h-4 rounded-full transition-all duration-500"
                  style={{ width: `${getCoveragePercentage()}%` }}
                ></div>
              </div>
              
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div className="text-center">
                  <div className="text-lg font-bold text-primary-600">{getCoveragePercentage()}%</div>
                  <div className="text-gray-600">Overall Coverage</div>
                </div>
                <div className="text-center">
                  <div className="text-lg font-bold text-success-600">{testResults.linesCovered || 0}</div>
                  <div className="text-gray-600">Lines Covered</div>
                </div>
                <div className="text-center">
                  <div className="text-lg font-bold text-warning-600">{testResults.linesTotal || 0}</div>
                  <div className="text-gray-600">Total Lines</div>
                </div>
                <div className="text-center">
                  <div className="text-lg font-bold text-info-600">{testResults.branchesCovered || 0}</div>
                  <div className="text-gray-600">Branches Covered</div>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default TestRunner;
